var mobile_stop_moved={init:function(selector,overflow_scrolling){$(selector).css("overflow","scroll");$(document).on("touchmove",function(e){e.preventDefault()});if(overflow_scrolling){$(selector).css("overflow","scroll").css("-webkit-overflow-scrolling","touch");$("body").on("touchstart",selector,function(e){var el=e.currentTarget;if(el.scrollTop===0){el.scrollTop=1}else{if(el.scrollHeight==el.scrollTop+el.offsetHeight){el.scrollTop=el.scrollTop-1}}});$("body").on("touchmove",selector,function(e){e.stopPropagation()})}}};